		/// <summary>
		/// Creates a new object that is a copy of the current instance.
		/// </summary>
		/// <returns>A new object that is a copy of this instance.</returns>
		public object Clone(IDictionary existingCopies)
		{
			if (existingCopies == null)
				existingCopies = new Hashtable();

			AddressEntityData _tmp = new AddressEntityData();

			_tmp.AddressId = this.AddressId;

			_tmp.AddressLine1 = this.AddressLine1;
			_tmp.AddressLine2 = this.AddressLine2;
			_tmp.City = this.City;
			_tmp.StateProvinceId = this.StateProvinceId;
			_tmp.PostalCode = this.PostalCode;
			_tmp.Rowguid = this.Rowguid;
			_tmp.ModifiedDate = this.ModifiedDate;

			#region Source Parent Composite Entities
			if (this.StateProvinceIdSource != null && existingCopies.Contains(this.StateProvinceIdSource))
				_tmp.StateProvinceIdSource = existingCopies[this.StateProvinceIdSource] as StateProvince;
			else
				_tmp.StateProvinceIdSource = MakeCopyOf(this.StateProvinceIdSource, existingCopies) as StateProvince;
			#endregion

			#region Child Collections
			//deep copy nested objects
			_tmp.CustomerAddressCollection = (TList<CustomerAddress>) MakeCopyOf(this.CustomerAddressCollection, existingCopies);
			_tmp.CustomerIdCustomerCollection_From_CustomerAddress = (TList<Customer>) MakeCopyOf(this.CustomerIdCustomerCollection_From_CustomerAddress, existingCopies);
			_tmp.SalesOrderHeaderCollectionGetByBillToAddressId = (TList<SalesOrderHeader>) MakeCopyOf(this.SalesOrderHeaderCollectionGetByBillToAddressId, existingCopies);
			_tmp.EmployeeAddressCollection = (TList<EmployeeAddress>) MakeCopyOf(this.EmployeeAddressCollection, existingCopies);
			_tmp.SalesOrderHeaderCollectionGetByShipToAddressId = (TList<SalesOrderHeader>) MakeCopyOf(this.SalesOrderHeaderCollectionGetByShipToAddressId, existingCopies);
			_tmp.VendorIdVendorCollection_From_VendorAddress = (TList<Vendor>) MakeCopyOf(this.VendorIdVendorCollection_From_VendorAddress, existingCopies);
			_tmp.VendorAddressCollection = (TList<VendorAddress>) MakeCopyOf(this.VendorAddressCollection, existingCopies);
			_tmp.EmployeeIdEmployeeCollection_From_EmployeeAddress = (TList<Employee>) MakeCopyOf(this.EmployeeIdEmployeeCollection_From_EmployeeAddress, existingCopies);
			#endregion Child Collections

			//EntityState
			_tmp.EntityState = this.EntityState;

			return _tmp;
		}

