				count = -1;
				if(reader.NextResult())
				{
					if(reader.Read())
					{
						count = reader.GetInt32(0);
					}
				}

				//Provider Data Requested Command Event
				OnDataRequested(new CommandEventArgs(commandWrapper, "GetByOrderId", tmp));
			}
			finally
			{
				if (reader != null)
					reader.Close();

				commandWrapper = null;
			}

			if (tmp.Count == 1)
			{
				return tmp[0];
			}
			else if (tmp.Count == 0)
