			bulkCopy.BulkCopyTimeout = 360;
			bulkCopy.DestinationTableName = "Contact";
			
			DataTable dataTable = new DataTable();
			DataColumn col0 = dataTable.Columns.Add("ContactID", typeof(System.Int32));
