			col13.AllowDBNull = false;		
			DataColumn col14 = dataTable.Columns.Add("ModifiedDate", typeof(System.DateTime));
			col14.AllowDBNull = false;		
			
			bulkCopy.ColumnMappings.Add("ContactID", "ContactID");
			bulkCopy.ColumnMappings.Add("NameStyle", "NameStyle");
			bulkCopy.ColumnMappings.Add("Title", "Title");
			bulkCopy.ColumnMappings.Add("FirstName", "FirstName");
