(

	@TerritoryId int    OUTPUT,

	@Name nvarchar (50)  ,

	@CountryRegionCode nvarchar (3)  ,

	@Group nvarchar (50)  ,

	@SalesYtd money   ,

	@SalesLastYear money   ,

	@CostYtd money   ,

