					(
					@Name
					,@CountryRegionCode
					,@Group
					,@SalesYtd
					,@SalesLastYear
					,@CostYtd
					,@CostLastYear
					,@ModifiedDate
					)
				
				SELECT @Rowguid=Rowguid	 from @IdentityRowGuids
