 entity.StringProperty = "foo";
 ProductSubcategory entity2 = new ProductSubcategory();
 entity.StringProperty = "bar";
 list.Add(entity);
 list.Add(entity2);
	ProductSubcategoryService.Update(list);
	}
	catch (Exception e)
	{
		if (DomainUtil.HandleException(e, name)) throw;
	}

